mapper.select:
select 

from  (
select s1.*,s2.name as parentName from sys_menu s1 left join sys_menu s2 on s1.parent_id=s2.id
)sys_menu
where id = #{id,jdbcType=VARCHAR}

  	 select 
    
    from (
    select s1.*,s2.name as parentName from sys_menu s1 left join sys_menu s2 on s1.parent_id=s2.id
    )sys_menu
      
     
     
	  order by ${orderField} ${orderSeq}
	 
	 
	 order by id  desc

  	 select 
    
    from  (
    select s1.*,s2.name as parentName from sys_menu s1 left join sys_menu s2 on s1.parent_id=s2.id
    )sys_menu
      
     
     
	  order by ${orderField} ${orderSeq}
	 
	 
	 order by id  desc
	 
	 limit #{startSize},#{pageSize}

select id, pId, pIds,name,delFlag,parentName  from (
SELECT S.id,S.parent_id as pId,S.parent_ids as pIds,S.name,Y.NAME AS parentName,S.del_flag as delFlag FROM 
sys_menu S LEFT JOIN sys_menu Y ON S.parent_id = Y.id
)sys_menu where 1=1
AND delFlag = #{delFlag} 
	AND pId = #{parentId}
